    private void jBtnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnCalcularActionPerformed
        // TODO add your handling code here:
        t1 = Double.parseDouble(jTxtTiempo1.getText());
        t2 = Double.parseDouble(jTxtTiempo2.getText());
        p1 = Double.parseDouble(jTxtPob1.getText());
        p2 = Double.parseDouble(jTxtPob2.getText());
        tMedio=Double.parseDouble(jTxtTempMedio.getText());

        k = Math.log((p2-tMedio) / (p1-tMedio)) / (t2 - t1);
        c = (p1-tMedio) / Math.pow(Math.E, t1 * k);

        String fc = formatearResultado(c);
        String fk = formatearResultado(k);
        jLbFuncion.setText("P(t) = " + fc + "e^(" + fk + "t) +"+tMedio);

    }//GEN-LAST:event_jBtnCalcularActionPerformed

    private void jBtnCalcularP3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnCalcularP3ActionPerformed
        // TODO add your handling code here:
        double p3, t3;
        t3 = Double.parseDouble(jTxtT3.getText());
        p3=c*Math.pow(Math.E, k*t3)+tMedio;
        String fP3=formatearResultado(p3);
        
        jLbPob3.setText(fP3); 
        
    }//GEN-LAST:event_jBtnCalcularP3ActionPerformed

