    private void jBtnT3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnT3ActionPerformed
        // TODO add your handling code here:
        double t3, p3;
        p3=Double.parseDouble(jTxtP3.getText());
        t3=Math.log((p3-tMedio)/c)/k;
        String fT3=formatearResultado(t3);
        jLbT3.setText(fT3+" "+jCbxTiempo.getSelectedItem().toString()); 
    }//GEN-LAST:event_jBtnT3ActionPerformed

